import {parseFromTimeZone, formatToTimeZone, listTimeZones } from 'date-fns';
import jwtDecode from 'jwt-decode';
import Moment from 'react-moment';

// Add padding from the start of the current string/number
export const myPadStart = (el, targetLength, padString) => {
   return el.toString().padStart(targetLength, padString);
};

// Insert something after every n characters in the string
export const chunker = (el, step, string) => {
   const regExp = new RegExp(`.{${step}}`, 'g');
   return el
      .toString()
      .match(regExp)
      .join(string);
};

// Format date

// List canonical time zone names: [ 'Africa/Abidjan', ... ]
const timeZones = listTimeZones()

// Set the date to "2018-09-01T16:01:36.386Z"
const utcDate = parseFromTimeZone('2018-09-01 18:01:36.386', { timeZone: 'Europe/Berlin' });

// Set the output to "1.9.2018 18:01:36.386 GMT+02:00 (CEST)"
const date = new Date('2018-09-01Z16:01:36.386Z')
const format = 'D.M.YYYY HH:mm:ss.SSS [GMT]Z (z)'

export const formatDate = formatToTimeZone(date, format, { timeZone: 'Europe/Berlin' });

// Token checker
export const getAuthToken = () => {
   return localStorage.getItem('token');
};

export const setAuthToken = token => {
   localStorage.setItem('token', token);
};

export const removeAuthToken = () => {
   localStorage.removeItem('token');
};

export const isValidToken = () => {
   return new Promise((resolve, reject) => {
      // Check if token is present
      const currentTime = Date.now().valueOf() / 1000;
      const token = getAuthToken();

      if (!token) {
         reject();
         return;
      }

      const decodedToken = jwtDecode(token);

      // If token expired - remove it
      if (decodedToken.exp < currentTime) {
         removeAuthToken();

         reject();
         return;
      }

      resolve(token);
   });
};
