import React from 'react';
import { Link } from 'react-router-dom';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import { makeStyles } from '@material-ui/core/styles';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Typography from '@material-ui/core/Typography'
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Button from '@material-ui/core/Button'


import Logo from '../../../assets/Images/argoslogo.svg';

const HomeHeader = props => {
   function ElevationScroll(props) {
      const { children } = props;

      const trigger = useScrollTrigger({
        disableHysteresis: true,
        threshold: 0
      });

      return React.cloneElement(children, {
        elevation: trigger ? 4 : 0,
      });
    }

    const useStyles = makeStyles(theme => ({
       toolbarMargin: {
          ...theme.mixins.toolbar,
          marginBottom: "3em"
       },
       Logo: {
         height: "7em",
         padding: "10px",
       },
       tabContainer: {
          marginLeft: "auto"
       },
       button : {
          borderRadius: "50px",
          textTransform: "none",
          height: "45px",
          color: theme.palette.primary,
          fontSize: "1rem",
          "&:hover": {
             backgroundColor: theme.palette.primary.light,
             color: "white"
          }    
         },
      logIn: {
         fontSize: "6em"
      }
    }))

    const classes = useStyles()

   return (
      <React.Fragment>
         <ElevationScroll>
            <AppBar position="fixed" color="transparent">
               <Toolbar>
                  <img src={Logo} alt="Argos Logo" className={classes.Logo}></img>
                  <Tabs className={classes.tabContainer}>
                     <Button className={classes.button} component={Link} to="/auth/login/" variant="contained" color="secondary">
                        <Typography className={classes.logIn} color="primary">
                           Log in
                        </Typography>
                     </Button>
                  </Tabs>

               </Toolbar>
            </AppBar>
         </ElevationScroll>
         <div className={classes.toolbarMargin}></div>
      </React.Fragment>

      // <header className="navigation-header">
      //    <div className="user-profile-box">
      //       <Link to="/panel/profile">
      //          <Logo />
      //          {/* <span>
      //             {props.user.firstName} {props.user.lastName}
      //          </span> */}
      //       </Link>
      //    </div>

      //    <ul className="navigation-header-links">
      //       <li>
      //          <Link to="/auth/login/">Login</Link>
      //       </li>
      //       <li>
      //          <Link to="/auth/create-user/">Register</Link>
      //       </li>
      //       <li className="toggle-menu">
      //          <button onClick={props.toggleMobileNav}>
      //             <i className="ion-navicon-round" />
      //          </button>
      //       </li>
      //    </ul>
      // </header>
   );
};

export default HomeHeader;
