import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button'

import LoginForm from '../../Authorization/Login/Form';
import RegisterFrom from '../../Authorization/Register/Form';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '5px solid theme.palette.primary.main',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    borderRadius: '25px',
    outline: "none",
    width: "25em"
  },
  button : {
    ...theme.typography.logIn,
    borderRadius: "50px",
    "&:hover": {
        backgroundColor: "theme.palette.primary.light",
        color: "white"
        }     
   }
}));

const LoginModal = props => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [logIn, setLogin] = React.useState(false)
  const [register, setRegister] = React.useState(true)

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleLogIn = () => {
      setLogin(true)
  }

  const handleRegister = () => {
    setRegister(true)
}

  return (
    <div>
       <Button onClick={handleOpen} className={classes.button} variant="contained" color="secondary">
         Log in
       </Button>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            {logIn ? <LoginForm /> : <RegisterFrom />}
          </div>
          {/* <div  onClick={handleLogIn} >
            <div className="inner-container">
                <div className="text">{ logIn ? "Register" : "Log in"}</div>
            </div>
          </div> */}
        </Fade>
      </Modal>
    </div>
  );
}

export default LoginModal;
