import React, { Component } from 'react';
import { Link } from 'react-router-dom';

import ReceiptsListEl from '../ListElement';
import { receipts } from '../../../Utils/Dummydata';




class ReceiptsList extends Component {
    constructor(props) {
       super(props);

       this.state = {
          search: '',
          receipts: receipts
       };
    }


    findReceipt = () => {
       this.setState({ search: this.refs.search.value });
    };

    render() {
       // Allow filtering by payee's name or transfer reference
        const searchText = this.state.search.toLowerCase();
        const receiptsList = receipts.filter(
            receipt =>
            receipt.type.toLowerCase().includes(searchText) ||
            receipt.reference.toLowerCase().includes(searchText)
        )
        .map((receipt, i) => (
            <ReceiptsListEl
            receipts={this.state.receipts} 
            total={receipts[i].total}
            matchUrl={this.props.match.url} />
        ));

      return (
         <div>
            <h1>Receipts</h1>

            <p>You have {receiptsList.length} Receipts right now!</p>
            <p>
               <Link to={`${this.props.match.url}/new`} className="btn btn-primary">
                  Export Receipts
               </Link>
            </p>

            <div className="form-group">
               <input
                  className="form-control"
                  placeholder="Search for receipts..."
                  onChange={this.findReceipt}
                  ref="search"
               />
            </div>

            <div className="list-group">{receiptsList}</div>
         </div>
      );
   }
}



export default ReceiptsList;
