import React from 'react';
import { makeStyles, useTheme} from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';

import * as actions from '../../../store/Actions/';

import { withFormik } from 'formik';
import * as Yup from 'yup';


const useStyles = makeStyles(theme => ({
   Logo: {
      height: "7em",
      padding: "10px",
    },
    button: {
      ...theme.typography.logIn,
      borderRadius: "40px",
      width: "15em",
      "&:hover": {
        backgroundColor: "theme.palette.primary.light",
        color: "white"
        }  
    }
}))


   // const theme = useTheme()


const innerForm = props => {


   const { classes,
           errors, 
           touched,
           isSubmitting,
           handleChange,
           handleBlur,
           handleSubmit,
           handleReset
          } = props;

   return (
      <Grid container direction="row">
         <Grid item container direction="column" justify="center" alignItems="center">
            <Grid item>
               <Grid container direction="column">
                  <Grid item>
                     <Typography align="center" color="primary" variant="h4">Log In</Typography>
                  </Grid>
                  <Grid item container direction="column" style={{maxWidth: "20em"}}>
                     {/* <Formik 
                        initialValues= {{email: '', password: ''}}
                        validationSchema={Yup.object({
                           email: Yup.string()
                              .email('Please enter a valid email address')
                              .required('Required')
                        })}
                        onSubmit={ (values, { setSubmitting }) => {
                           setTimeout(() => {
                              alert(JSON.stringify(values, null, 2));
                              setSubmitting(false);
                           }, 400);     
                        }}
                     > */}
                     {/* {formik => (  */}
                     <form onSubmit={handleSubmit}>
                        <Grid item>
                           <TextField 
                              required
                              fullWidth
                              label="Email" 
                              id="email"
                              type="email"
                              name="email"
                              value={values.email}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              helperText={touched.email && Boolean(errors.email)}
                              style={{marginTop: "2em"}} 
                           />
                        </Grid>
                        {touched.email && errors.email ? (<div>{errors.email}</div>) : null}
                        <Grid item>
                           <TextField 
                              required
                              fullWidth
                              label="Password" 
                              id="password"
                              name="password"         
                              type="password"
                              value={values.password}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              helperText={touched.password && Boolean(errors.password)}
                              autoComplete="current-password"
                              style={{marginTop: "2em"}}
                           />
                        </Grid>
                        {touched.password && errors.password ? (<div>{errors.password}</div>) : null}
                        <Grid item container justify="center" style={{ marginTop: "2em" }}>
                           <Button 
                              className={classes.button}
                              variant= "contained" 
                              type="submit"
                              disabled={isSubmitting} 
                              color="secondary">
                                 Log in
                           </Button>
                        </Grid>
                     </ form>
                     {/* )} */}
                     {/* </Formik> */}
                  </Grid>
               </Grid>
            </Grid>
         </Grid>
      </Grid>
   )
};

const LoginForm = withFormik({
   // Transform outer props into form values
   mapPropsToValues: ({ 
         email,
         password 
      }) => {
         return {
            email: '',
            password: ''
         };
      },

   // Add a custom validation function (this can be async too!)
   validationSchema: Yup.object().shape({
      email: Yup.string()
         .required('Email is required')
         .email('This is not a valid email'),
      password: Yup.string()
         .required('Password is required')
         .min(6, 'Your password has to be at least 6 characters')
   }),

   // Submission handler
   handleSubmit: (values, { setSubmitting }) => {
      setTimeout(() => {
         // Submit to sever
         alert(JSON.stringify(values, null, 2));
         setSubmitting(false);
      }, 1000);
   }
})(innerForm);

export default LoginForm;
