import React from 'react';

import TransactionDetails from './TransactionDetails/TransactionDetails';
import Date from './Date/Date';
import Offer from './Offer/Offer';
import Barcode from './Barcode/Barcode';
import MerchantSurvey from './Survey/Survey';

// import { formatDate } from '../../../Tools';

const SingleReceipt = ({ singleReceipt: { 
   type, 
   merchant_logo,
   merchant_address,
   merchant_phone,
   item_name,
   item_number,
   item_category,
   tax_amount,
   sub_total,
   total
   } 
}) => {
      return (
         <section className="module single-transfer">
            <img src={merchant_logo} className="App-logo" alt="logo" />
            <h1>{type}</h1>
            <div className="store-details">
               <p>{merchant_address} - {merchant_phone}</p>
            </div>
            <Date />
            <Barcode 
               value={this.state.value}
               width={this.state.width}
               height={this.state.height}
               format={this.state.format}
               displayValue={this.state.displayValue}
               fontOptions={this.state.fontOptions}
               font={this.state.font}
               textAlign={this.state.center}
               textPosition={this.state.textPosition}
               textMargin={this.state.textMargin}
               fontSize={this.state.fontSize}
               background={this.state.background}
               lineColor={this.state.lineColor}
               margin={this.state.margin}
               marginTop={this.state.marginTop}
               marginBottom={this.state.marginBottom}
               marginLeft={this.state.marginLeft}
               marginRight={this.state.marginRight}
            />
            <TransactionDetails 
               itemCategory={item_category} 
               itemNumber={item_number}
               itemName={item_name}
               itemSubtotal={sub_total}
               transactionTax="T = MA TAX 6.2500% on"
               itemTotal={total}
               taxAmount={tax_amount}
            />
            <hr className="divider"></hr>
            <Offer />
            <hr className="divider"></hr>
            <img src={merchant_logo} className="App-logo" alt="logo" />
            <div className="store-details">
                  <p>{merchant_address} - {merchant_phone}</p>
            </div>
            <Date />
            <hr className="divider"></hr>  
            <div className="surveyjs">
               <MerchantSurvey />
            </div>
      </section>
      )
};



const mapStateToProps = (state, ownProps) => {
   const { transId } = ownProps

   return {
      singleTrans: state.transfers.data.find(el => el._id === transId)
   };
};

export default connect(mapStateToProps)(SingleReceipt);
