import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as Actions from '../../../Actions';
import withAuth from '../../../Hoc/WithAuth';
import Input from '../../../Components/UI/FormBoxes/Input/Input';

import RegisterBox from '../../../Components/Authorization/Register';

class Register extends Component {
   state = {
      controls: {
         firstName: {
            elementType: 'input',
            elementConfig: {
                type: 'text',
                placeholder: 'Enter your first name'
            },
            value: '',
            validation: {
                required: true,
                minLength: 5,
                maxLength: 255
            },
            valid: false,
            touched: false
        },
        lastName: {
            elementType: 'input',
            elementConfig: {
               type: 'text',
               placeholder: 'Enter your last name'
            },
            value: '',
            validation: {
               required: true,
               minLength: 5,
               maxLength: 255
            },
            valid: false,
            touched: false
         },
         email: {
            elementType: 'input',
            elementConfig: {
               type: 'email',
               placeholder: 'Enter your email address'
            },
            value: '',
            validation: {
               required: true,
               isEmail: true
            },
            valid: false,
            touched: false
         },
         password: {
            elementType: 'input',
            elementConfig: {
               type: 'password',
               placeholder: 'Enter your password'
            },
            value: '',
            validation: {
               required: true,
               minLength: 6
            },
            valid: false,
            touched: false
         },
      },
      loading: false,
      error: null
   };

   orderHandler = (event) => {
      event.preventDefault();
      const formData = {};
      for (let formElementIdentifer in this.state.orderForm) {
          formData[formElementIdentifer] = this.state.orderForm[formElementIdentifer].value
      }
      const order = {
          ingredients: this.props.ingredients,
          price: this.props.price,
          orderData: formData
      }
  }

  checkValidation(value, rules) {
      let isValid = true;
      if (rules.required) {
          isValid = value.trim() !=='' && isValid;
      }
      if (rules.minLength) {
          isValid = value.length >= rules.minLength && isValid
      }
      if (rules.maxLength) {
          isValid = value.length <= rules.maxLength && isValid
      }
      if (rules.isEmail) {
         const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
         isValid = pattern.test(value) && isValid
      }
     if (rules.isNumeric) {
         const pattern = /^\d+$/;
         isValid = pattern.test(value) && isValid
      }
      return isValid;
  }

  inputChangedHandler =(event, inputIdentifier) => {
      const updatedOrderForm = {
          ...this.state.orderForm
      }
      const updatedFormElement ={
          ...updatedOrderForm[inputIdentifier] 
      };
      updatedFormElement.value = event.target.value;
      updatedFormElement.valid = this.checkValidation(updatedFormElement.value, updatedFormElement.validation)
      updatedFormElement.touched = true;
      updatedOrderForm[inputIdentifier] = updatedFormElement;

      let formIsValid = true;
      for (let inputIdentifier in updatedOrderForm) {
          formIsValid = updatedOrderForm[inputIdentifier].valid && formIsValid
      }
      this.setState({orderForm: updatedOrderForm, formIsValid: formIsValid});
  }

   render() {
      const formElementsArray = []
      for (let key in this.state.controls) {
          formElementsArray.push({
              id: key,
              config: this.state.controls[key]
          });
      }

      const form = formElementsArray.map(formElement => (
         <Input
            key={formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value} 
            changed={(event) => this.inputChangedHandler(event, formElement.id)}
            invalid={!formElement.config.valid}
            touched={formElement.config.touched}
         />
      ));

      return (
         <div className="row">
            <div className="col">
               <RegisterBox
                  history={this.props.history}
                  onRegisterSubmit={this.onRegisterSubmit}
                  loading={this.state.loading}
                  error={this.state.error}
               > {form}</RegisterBox>

            </div>
         </div>
      );
   }

   onRegisterSubmit = (email, password, user_type) => {
      this.setState({ loading: true, error: null });

      // Dispatch auth action
      // There will be automatic redirect to panel, in HOC
      this.props
         .register({ email, password, user_type })
         .catch(error => this.setState({ loading: false, error }));
   };
}

const mapDispatchToProps = dispatch => {
   return {
      register: (name, email, password, user_type) => dispatch(Actions.register(name, email, password, user_type))
   };
};

export default connect(null, mapDispatchToProps)(withAuth(Register));
