import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as Actions from '../../../Actions';
import withAuth from '../../../Hoc/WithAuth';

import RegisterBox from '../../../Components/Authorization/Register';

class Register extends Component {
   state = {
      controls: {
         firstName: {
            elementType: 'input',
            elementConfig: {
                type: 'text',
                placeholder: 'Enter your first name'
            },
            value: '',
            validation: {
                required: true,
                minLength: 5,
                maxLength: 255
            },
            valid: false,
            touched: false
        },
        lastName: {
            elementType: 'input',
            elementConfig: {
               type: 'text',
               placeholder: 'Enter your last name'
            },
            value: '',
            validation: {
               required: true,
               minLength: 5,
               maxLength: 255
            },
            valid: false,
            touched: false
         },
         email: {
            elementType: 'input',
            elementConfig: {
               type: 'email',
               placeholder: 'Enter your email address'
            },
            value: '',
            validation: {
               required: true,
               isEmail: true
            },
            valid: false,
            touched: false
         },
         password: {
            elementType: 'input',
            elementConfig: {
               type: 'password',
               placeholder: 'Enter your password'
            },
            value: '',
            validation: {
               required: true,
               minLength: 6
            },
            valid: false,
            touched: false
         },
      },
      loading: false,
      error: null
   };

   render() {

      return (
         <div className="row">
            <div className="col">
               <RegisterBox
                  history={this.props.history}
                  onRegisterSubmit={this.onRegisterSubmit}
                  loading={this.state.loading}
                  error={this.state.error}
               />
            </div>
         </div>
      );
   }

   onRegisterSubmit = (email, password, user_type) => {
      this.setState({ loading: true, error: null });

      // Dispatch auth action
      // There will be automatic redirect to panel, in HOC
      this.props
         .register({ email, password, user_type })
         .catch(error => this.setState({ loading: false, error }));
   };
}

const mapDispatchToProps = dispatch => {
   return {
      register: (name, email, password, user_type) => dispatch(Actions.register(name, email, password, user_type))
   };
};

export default connect(null, mapDispatchToProps)(withAuth(Register));
