import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Grid from '@material-ui/core/Grid';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button'
import Paper from '@material-ui/core/Paper';

import LoginForm from '../../Authorization/Login/Form';
import RegisterBox from '../../Authorization/Register/index';
import { Typography } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '5px solid theme.palette.primary.main',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    borderRadius: '25px',
    outline: "none",
    width: "25em"
  },
  toggleLogin: {
      backgroundColor: theme.palette.primary.main,
      color: "white",
      position: "absolute",
      top: "30%",
      left: "62.5%",
      width: "10%",
      borderRadius: "20px",
      height: "40%",
      cursor: "pointer",
      transition: `all 400ms ease-in-out`,
      justifyContent: "center",
      alignItems: "center",
  },
  toggleRegister: {
    backgroundColor: theme.palette.primary.main,
    color: "white",
    position: "absolute",
    top: "30%",
    left: "-62.5%",
    width: "10%",
    borderRadius: "20px",
    height: "40%",
    cursor: "pointer",
    transition: `all 400ms ease-in-out`,
    justifyContent: "center",
    alignItems: "center",
},
  rightSide: {
    display: "flex",
    flexDirection: "column",


    backgroundColor: "#3498db",
    zIndex: 1,
    boxShadow: `0px 0px 12px 2px rgba(15, 15, 15, 0.281)`,
    right: {
        right: "-30%",
        alignItems: "flex-start",
        "&:hover": {
            right: "-45%"
      }
    },
    left: {
        left: "30%",
        alignItems: "flex-start",
        "&:hover": {
            right: "45%"
      }
    } 
    },
  button : {
    ...theme.typography.logIn,
    borderRadius: "50px",
    "&:hover": {
        backgroundColor: "theme.palette.primary.light",
        color: "white"
        }     
   }
}));

const LoginModal = props => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [logIn, setLogin] = React.useState(true)
  const [register, setRegister] = React.useState(false)

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleLogIn = () => {
      setLogin( prevState => !prevState)
  }

  const handleRegister = () => {
    setRegister(true)
}

let AttachedClasses = [classes.toggleLogin]

if (logIn) {
    AttachedClasses = [classes.toggleLogin]
} 


  return (
    <div>
       <Button onClick={handleOpen} className={classes.button} variant="contained" color="secondary">
         Log in
       </Button>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            {logIn ? <LoginForm /> : <RegisterBox />}
            <Grid item container direction="column" alignItems="center" justify="center" onClick={handleLogIn}>
                <Grid item>
                     <Paper className={AttachedClasses} elevation={3}>
                        <Typography align="center">
                            Register
                        </Typography>
                     </Paper>
                </Grid>    
            </Grid> 
          </div>
        </Fade> 
      </Modal>
    </div>
  );
}

export default LoginModal;
