//   handleRemoveLineItem = (elementIndex) => (event) => {
//     this.setState({
//       lineItems: this.state.lineItems.filter((item, i) => {
//         return elementIndex !== i
//       })
//     })
//   }

//   handleReorderLineItems = (newLineItems) => {
//     this.setState({
//       lineItems: newLineItems,
//     })
//   }

  const handleFocusSelect = (event) => {
    event.target.select()
  }

  const handlePayButtonClick = () => {
    alert('Not implemented')
  }

   const formatCurrency = (amount) => {
    return (new Intl.NumberFormat(locale, {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(amount))
  }

  const calcTaxAmount = (c) => {
    return c * (taxRate / 100)
  }

  const calcLineItemsTotal = () => {
    return lineItems.reduce((prev, cur) => (prev + (cur.quantity * cur.price)), 0)
  }

  const calcTaxTotal = () => {
    return calcLineItemsTotal() * (taxRate / 100)
  }

  const calcGrandTotal = () => {
    return calcLineItemsTotal() + calcTaxTotal()
  }


const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
      },
      toolbarMargin: {
        ...theme.mixins.toolbar,
        marginBottom: "3em"
     },
      toolbar: {
        paddingRight: 24, // keep right padding when drawer closed
      },
      toolbarIcon: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'flex-end',
        padding: '0 8px',
        ...theme.mixins.toolbar,
      },
      appBarSpacer: theme.mixins.toolbar,
      content: {
        flexGrow: 1,
        height: '100vh',
        overflow: 'auto',
      },
 }))
 const classes = useStyles()
 const theme = useTheme()



    return (


      <Grid container alignItems="center" justify="center">
          <Grid container direction="column" alignItems="center" justify="center">
      <CssBaseline />
      <AppBar position="absolute">
        <Toolbar className={classes.toolbar}>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
            Receipt Builder
          </Typography>
          <IconButton color="inherit">
            <Badge badgeContent={4} color="secondary">
              <NotificationsIcon />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
      <div className="classes.toolbarMargin"></div>
      <Paper>
        <Grid item>
          <img src="https://via.placeholder.com/150x50.png?text=logo" alt="Logo" />
        </Grid>
        <div>
          <Grid container direction="column" justify="center" alignContent="center">
            <strong>Amazing Company</strong><br />
              123 Kensington Ave<br />
              Toronto, ON, Canada &nbsp;A1B2C3<br />
              416-555-1234
          </Grid>
          <div>
            <div>
              <div>
                <div>Customer #</div>
                <div>123456</div>
              </div>
              <div>
                <div>Invoice #</div>
                <div>123456</div>
              </div>
              <div>
                <div>Date</div>
                <div>2019-01-01</div>
              </div>
            </div>
          </div>
        </div>
        <h2>Invoice</h2>

          {/* <LineItems
            items={this.state.lineItems}
            currencyFormatter={this.formatCurrency}
            addHandler={this.handleAddLineItem}
            changeHandler={this.handleLineItemChange}
            focusHandler={this.handleFocusSelect}
            deleteHandler={this.handleRemoveLineItem}
            reorderHandler={this.handleReorderLineItems}
          /> */}

        <div >
          <form>
            <div >
              <div >
                <div>Tax Rate (%)</div>
                <div>
                    <input 
                        name="taxRate" 
                        type="number" 
                        step="0.01" 
                        value={taxRate} 
                        // onChange={handleInvoiceChange} 
                        onFocus={handleFocusSelect} 
                        />
                </div>
              </div>
            </div>
          </form>
          <form>
            <div>
              <div>
                <div>Subtotal</div>
                <div>{formatCurrency(calcLineItemsTotal())}</div>
              </div>
              <div>
                <div>Tax ({taxRate}%)</div>
                <div>{formatCurrency(calcTaxTotal())}</div>
              </div>
              <div>
                <div>Total Due</div>
                <div>{formatCurrency(calcGrandTotal())}</div>
              </div>
            </div>
          </form>
        </div>

        <div>
          <button onClick={handlePayButtonClick}>Pay Now</button>
        </div>

        <div >
          <div>
            <h4>Notes</h4>
            <p>Demo by Kevin Firko, independent consulting developer at <a href="https://bitcurve.com">Bitcurve</a>.</p>
            <p>Check out my blog <a href="https://firxworx.com">https://firxworx.com</a> and <a href="https://firxworx.com/blog/coding/creating-an-invoice-component-with-dynamic-line-items-using-react/">the related post</a> covering how to create a dynamic Invoice component in React. </p>
            <p>Find the <a href="https://github.com/firxworx/react-simple-invoice">code on Github</a>.</p>
          </div>
          <div>
            <div>Thank-you for your business</div>
          </div>
        </div>
        </Paper>
        </Grid>
     </Grid>

    )



}

export default ReceiptTemplate;
