import { createMuiTheme } from '@material-ui/core/styles';

const argosBlue = "#2B5597";
const argosYellow = "#FFC629";

export default createMuiTheme({
    palette: {
        background: {
            default: "rgba(240,242,245,1)"
        },
        common: {
            argosBlue: `${argosBlue}`,
            argosYellow: `${argosYellow}`
        },
        primary: {
            main: `${argosBlue}`
        },
        secondary: {
            main: `${argosYellow}`
        }
    },
    typography: {
        tab: {
            fontSize: "1rem"
        },
        h1: {
            lineHeight: 1.5
        }
    }   
});
