    <div className={classes.root}>
      <Grid container justify="center" spacing={3}>
      <Paper className={classes.paper}>
        <Grid item xs={12} justify="center">
          <img className={classes.logo} src="https://via.placeholder.com/150x50.png?text=logo" alt="Logo" />
        </Grid>
        <Grid item xs={12} justify="center">
          <Typography>1 Mystic View Road</Typography>
        </Grid>
        <Grid item xs={12} justify="center">
            <Typography>Everett, MA 02149</Typography>
        </Grid>
        <Grid item xs={12} justify="center">
            <Typography>617-420-0000</Typography>
