import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
// import { formatDate } from '../../../Tools';

const ReceiptsListEl = ({ receipts }) => {
//    date = formatDate(date, 'dd/MM/yyyy HH:mm');
const receiptListEl = receipts.map((receipt, i) => {
    return <Link  className="list-group-item list-group-item-action">
                <div className="d-flex w-100 justify-content-between">
                    <h5 className="mb-1">{receipts[i].type} Receipt</h5>
                    <b>amount:</b> {receipts[i].total}
                </div>
                <p className="mb-1">
                    <b>Payee:</b> {receipts[i].payeeName} / 
                </p>
                <small className="text-muted">Reference: {receipts[i].reference}</small>
            </Link>
 })

   return (
        <div>
            {receiptListEl}
        </div>
   );
};

ReceiptsListEl.propTypes = {
   matchUrl: PropTypes.string,
   _id: PropTypes.string,
   type: PropTypes.string,
   payeeName: PropTypes.string,
   date: PropTypes.string,
   total: PropTypes.number,
   status: PropTypes.string
};

export default ReceiptsListEl;
