import React from './node_modules/react';
// import { formatDate } from '../../../Tools';

const SingleReceipt = ({ receipts } ) => {
   // date = formatDate(date, 'dd/MM/yyyy HH:mm');
   const singleReceipt = receipts.map((receipt, i)=> {
      return (
         <section className="module single-transfer">
         <img src={brandlogo} className="App-logo" alt="logo" />
         <h1>{receipts[i].type}</h1>
         <div className="store-details">
            <p>{storeLocation} - {phoneNumber}</p>
        </div>
        <Date />
        <Barcode 
          value={this.state.value}
          width={this.state.width}
          height={this.state.height}
          format={this.state.format}
          displayValue={this.state.displayValue}
          fontOptions={this.state.fontOptions}
          font={this.state.font}
          textAlign={this.state.center}
          textPosition={this.state.textPosition}
          textMargin={this.state.textMargin}
          fontSize={this.state.fontSize}
          background={this.state.background}
          lineColor={this.state.lineColor}
          margin={this.state.margin}
          marginTop={this.state.marginTop}
          marginBottom={this.state.marginBottom}
          marginLeft={this.state.marginLeft}
          marginRight={this.state.marginRight}
        />
          <main className="transaction-details" >
          <TransactionDetails 
            itemCategory="HEALTH-BEAUTY-COSMETICS" 
            itemNumber="245070170"
            itemName="UPUP ADHESIV"
            itemSubtotal="$1.89"
            transactionTax="T = MA TAX 6.2500% on"
            itemTotal="$2.01"
            taxAmount="$0.12"

          />
        </main>
        <hr className="divider"></hr>
        <Offer />
        <hr className="divider"></hr>
        <img src={brandlogo} className="App-logo" alt="logo" />
        <div className="store-details">
            <p>{storeLocation} - {phoneNumber}</p>
        </div>
        <Date />
        <hr className="divider"></hr>

        <div className="surveyjs">
          <SurveyReceipt />
        </div>
      </section>
      )
   });

   return (
      <div>
         {singleReceipt}
      </div>

   );
};



export default SingleReceipt;
