  const [locale, setLocale] = useState('en-US')
  const [taxRate, setTaxRate] = useState(0.00)
  const [lineItems, setLineItems] = useState([
    {
      id: 'initial',      // react-beautiful-dnd unique key
      name: '',
      description: '',
      quantity: 0,
      price: 0.00,
