import React from 'react';
import { connect } from 'react-redux';
import CardInfobox from '../CardInfoBox';

const SingleCard = ({ bankcards, match }) => {
   return (
      <div className="row">
         <div className="col">
            <CardInfobox {...bankcards} currentUrl={match.url} />
         </div>
      </div>
   );
};

const mapStateToProps = (state, ownProps) => {
   const cardId = ownProps.match.params.cardId;

   return {
      bankcards: state.cards.data.find(el => el.id === cardId)
   };
};

export default connect(mapStateToProps)(SingleCard);
