import React, { Component } from 'react';
import { makeStyles, useTheme} from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import CssBaseline from '@material-ui/core/CssBaseline';
import MenuIcon from '@material-ui/icons/Menu';
import NotificationsIcon from '@material-ui/icons/Notifications';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';

// import styles from './Invoice.module.scss'

import LineItems from './LineItems'

// import uuidv4 from 'uuid/v4'




class ReceiptTemplate extends Component {

  locale = 'en-US'
  currency = 'USD'

  state = {
    taxRate: 0.00,
    lineItems: [
      {
        id: 'initial',      // react-beautiful-dnd unique key
        name: '',
        description: '',
        quantity: 0,
        price: 0.00,
      },
    ]
  }

  handleInvoiceChange = (event) => {
    this.setState({[event.target.name]: event.target.value})
  }

  handleLineItemChange = (elementIndex) => (event) => {
    let lineItems = this.state.lineItems.map((item, i) => {
      if (elementIndex !== i) return item
      return {...item, [event.target.name]: event.target.value}
    })
    this.setState({lineItems})
  }

  handleAddLineItem = (event) => {
    this.setState({
      // use optimistic uuid for drag drop; in a production app this could be a database id
      lineItems: this.state.lineItems.concat(
        [{ id: 1, name: '', description: '', quantity: 0, price: 0.00 }]
      )
    })
  }

  handleRemoveLineItem = (elementIndex) => (event) => {
    this.setState({
      lineItems: this.state.lineItems.filter((item, i) => {
        return elementIndex !== i
      })
    })
  }

  handleReorderLineItems = (newLineItems) => {
    this.setState({
      lineItems: newLineItems,
    })
  }

  handleFocusSelect = (event) => {
    event.target.select()
  }

  handlePayButtonClick = () => {
    alert('Not implemented')
  }

  formatCurrency = (amount) => {
    return (new Intl.NumberFormat(this.locale, {
      style: 'currency',
      currency: this.currency,
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(amount))
  }

  calcTaxAmount = (c) => {
    return c * (this.state.taxRate / 100)
  }

  calcLineItemsTotal = () => {
    return this.state.lineItems.reduce((prev, cur) => (prev + (cur.quantity * cur.price)), 0)
  }

  calcTaxTotal = () => {
    return this.calcLineItemsTotal() * (this.state.taxRate / 100)
  }

  calcGrandTotal = () => {
    return this.calcLineItemsTotal() + this.calcTaxTotal()
  }



  render = () => {
    const classes = useStyles()

    return (

      <Grid container>
      <CssBaseline />
      <AppBar position="absolute">
        <Toolbar className={classes.toolbar}>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
            Receipt Builder
          </Typography>
          <IconButton color="inherit">
            <Badge badgeContent={4} color="secondary">
              <NotificationsIcon />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
        <div>
          <img src="https://via.placeholder.com/150x50.png?text=logo" alt="Logo" />
        </div>
        <div>
          <div>
            <strong>Amazing Company</strong><br />
              123 Kensington Ave<br />
              Toronto, ON, Canada &nbsp;A1B2C3<br />
              416-555-1234
          </div>
          <div>
            <div>
              <div>
                <div>Customer #</div>
                <div>123456</div>
              </div>
              <div>
                <div>Invoice #</div>
                <div>123456</div>
              </div>
              <div>
                <div>Date</div>
                <div>2019-01-01</div>
              </div>
            </div>
          </div>
        </div>
        <h2>Invoice</h2>

          <LineItems
            items={this.state.lineItems}
            currencyFormatter={this.formatCurrency}
            addHandler={this.handleAddLineItem}
            changeHandler={this.handleLineItemChange}
            focusHandler={this.handleFocusSelect}
            deleteHandler={this.handleRemoveLineItem}
            reorderHandler={this.handleReorderLineItems}
          />

        <div >
          <form>
            <div >
              <div >
                <div>Tax Rate (%)</div>
                <div><input name="taxRate" type="number" step="0.01" value={this.state.taxRate} onChange={this.handleInvoiceChange} onFocus={this.handleFocusSelect} /></div>
              </div>
            </div>
          </form>
          <form>
            <div>
              <div>
                <div>Subtotal</div>
                <div>{this.formatCurrency(this.calcLineItemsTotal())}</div>
              </div>
              <div>
                <div>Tax ({this.state.taxRate}%)</div>
                <div>{this.formatCurrency(this.calcTaxTotal())}</div>
              </div>
              <div>
                <div>Total Due</div>
                <div>{this.formatCurrency(this.calcGrandTotal())}</div>
              </div>
            </div>
          </form>
        </div>

        <div>
          <button onClick={this.handlePayButtonClick}>Pay Now</button>
        </div>

        <div >
          <div>
            <h4>Notes</h4>
            <p>Demo by Kevin Firko, independent consulting developer at <a href="https://bitcurve.com">Bitcurve</a>.</p>
            <p>Check out my blog <a href="https://firxworx.com">https://firxworx.com</a> and <a href="https://firxworx.com/blog/coding/creating-an-invoice-component-with-dynamic-line-items-using-react/">the related post</a> covering how to create a dynamic Invoice component in React. </p>
            <p>Find the <a href="https://github.com/firxworx/react-simple-invoice">code on Github</a>.</p>
          </div>
          <div>
            <div>Thank-you for your business</div>
          </div>
        </div>

     </Grid>

    )
    const useStyles = makeStyles(theme => ({
        Logo: {
           height: "7em",
           padding: "10px",
         },
         button: {
           ...theme.typography.logIn,
           borderRadius: "40px",
           width: "15em",
           "&:hover": {
             backgroundColor: "theme.palette.primary.light",
             color: "white"
             }  
         }
     }))

    const theme = useTheme()
  }

}

export default ReceiptTemplate;
