import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button'

import LoginForm from '../../Authorization/Login/Form';
import RegisterBox from '../../Authorization/Register/index';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: '5px solid theme.palette.primary.main',
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    borderRadius: '25px',
    outline: "none",
    width: "25em"
  },
  rightSide: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    height: "40%",
    backgroundColor: "#3498db",
    width: "10%",
    position: "absolute",
    top: "35%",
    right: "55%",
    borderRadius: "6px",
    zIndex: 1,
    transition: `all 400ms ease-in-out`,
    cursor: "pointer",
    boxShadow: `0px 0px 12px 2px rgba(15, 15, 15, 0.281)`,
    right: {
        top: "30%",
        alignItems: "flex-start",
        "&:hover": {
            right: "-45%"
      }
    },
    left: {
        left: "30%",
        alignItems: "flex-start",
        "&:hover": {
            right: "45%"
      }
    } 
    },
  button : {
    ...theme.typography.logIn,
    borderRadius: "50px",
    "&:hover": {
        backgroundColor: "theme.palette.primary.light",
        color: "white"
        }     
   }
}));

const LoginModal = props => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);
  const [logIn, setLogin] = React.useState(true)
  const [register, setRegister] = React.useState(false)

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleLogIn = () => {
      setLogin( prevState => !prevState)
  }

  const handleRegister = () => {
    setRegister(true)
}

let AttachedClasses = [classes.rightSide]

if (logIn) {
    AttachedClasses = [ classes.rightSide, classes.rightSide.right]
} 

if (!logIn) {
    AttachedClasses = [ classes.rightSide, classes.rightSide.left]
}

  return (
    <div>
       <Button onClick={handleOpen} className={classes.button} variant="contained" color="secondary">
         Log in
       </Button>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            {logIn ? <LoginForm /> : <RegisterBox />}
            <div className={AttachedClasses.join(' ')} onClick={handleLogIn} >
                <div className="inner-container">
                    <div className="text">Register</div>
                </div>
            </div>
          </div>

        </Fade>

      </Modal>
    </div>
  );
}

export default LoginModal;
