import React, { Fragment, useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { useTheme} from '@material-ui/core/styles';

import HomeRoundedIcon from '@material-ui/icons/HomeRounded';
import ReceiptRoundedIcon from '@material-ui/icons/ReceiptRounded';
import CreditCardRoundedIcon from '@material-ui/icons/CreditCardRounded';
import PersonRoundedIcon from '@material-ui/icons/PersonRounded';

import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import IconButton from '@material-ui/core/IconButton';
import MenuRoundedIcon from '@material-ui/icons/MenuRounded';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

import './style.scss';
import Logo from '../../../assets/Images/argoslogo.svg';

function ElevationScroll(props) {
   const { children } = props;
   const trigger = useScrollTrigger({
     disableHysteresis: true,
     threshold: 0
   });

   return React.cloneElement(children, {
     elevation: trigger ? 4 : 0,
   });
 }

 const useStyles = makeStyles(theme => ({
    toolbarMargin: {
       ...theme.mixins.toolbar,
       marginBottom: "3em"
    },
    logoContainer: {
       "&:hover": {
          backgroundColor: "transparent"
       }
    },
    Logo: {
      height: "7em",
      padding: "10px",
      [theme.breakpoints.down("md")]: {
         height: "6em"
      },
      [theme.breakpoints.down("xs")]: {
         height: "5.5em"
      }
    },
    tabContainer: {
       marginLeft: "auto"
    },
    tab: {
      ...theme.typography.tab
    },
    drawerIconContainer: {
       "&:hover": {
          backgroundColor: "transparent"
       },
       marginLeft: "auto"
    },
    drawerIcon: {
       height: "50px",
       width: "50px"
    },
    drawer: {
       backgroundColor: theme.palette.common.argosBlue
    }
 }))


const MainNavigation = props => {
   const classes = useStyles();
   const theme = useTheme();
   const iOS = process.browser && /iPad|iPhone|iPod/.test(navigator.userAgent);
   const matches = useMediaQuery(theme.breakpoints.down("md"));

   const [openDrawer, setOpenDrawer] = useState(false);
   const [value, setValue] = useState(0);


   const handleChange = (e, newValue) => {
      setValue(newValue);
   }

   useEffect(() => {
      if (window.location.pathname === "/panel" && value!==0) {
         setValue(0)
      } else if (window.location.pathname === "/panel/receipts" && value !== 1) {
         setValue(1)
      } else if (window.location.pathname === "/panel/cards" && value !== 2) {
         setValue(2)
      }
         else if (window.location.pathname === "/panel/profile" && value !== 3) {
            setValue(3)   
         }
      }, [value]);

   const tabs = (
      <Fragment>
         <Tabs value={value} className={classes.tabContainer} onChange={handleChange}>
            <Tab className={classes.tab} component={Link} to="/panel" icon={<HomeRoundedIcon />} label="Home" />
            <Tab className={classes.tab} component={Link} to="/panel/receipts" icon={<ReceiptRoundedIcon />} label="Receipts" />
            <Tab className={classes.tab} component={Link} to="/panel/cards" icon={<CreditCardRoundedIcon />} label="Cards" />
            <Tab className={classes.tab} component={Link} to="/panel/profile"  icon={<PersonRoundedIcon />}  label="Profile" />
         </Tabs>
      </Fragment>
   );

   const drawer = (
      <Fragment>
         <SwipeableDrawer 
            disableBackdropTransition={!iOS} 
            disableDiscovery={iOS}
            open={openDrawer}
            onClose={() => setOpenDrawer(false)}
            onOpen={() => setOpenDrawer(true)}
            classes={{paper: classes.drawer}}
            >
               <List>
                  <ListItem onClick={() => setOpenDrawer(false)}  divider button component={Link} to="/panel">
                     <ListItemText disableTypography>Home</ListItemText>
                  </ListItem>
                  <ListItem onClick={() => setOpenDrawer(false)}  divider button component={Link} to="/panel/receipts">
                     <ListItemText disableTypography>Receipts</ListItemText>
                  </ListItem>
                  <ListItem onClick={() => setOpenDrawer(false)}  divider button component={Link} to="/panel/cards">
                     <ListItemText disableTypography>Cards</ListItemText>
                  </ListItem>
                  <ListItem onClick={() => setOpenDrawer(false)}  divider button component={Link} to="/panel/profile">
                     <ListItemText disableTypography>Profile</ListItemText>
                  </ListItem>
               </List>
         </SwipeableDrawer> 
         <IconButton className={classes.drawerIconContainer} onClick={ () => setOpenDrawer(!openDrawer)} disableRipple>
            <MenuRoundedIcon color= "secondary" className={classes.drawerIcon} />
         </IconButton>
      </Fragment>
   );

   return (
      <Fragment>
          <ElevationScroll>
            <AppBar>
               <Toolbar disableGutters>
                  <Button 
                     className={classes.logoContainer}
                     component={Link} 
                     to="/panel"
                     disableRipple 
                     onClick={() => setValue(0)}>
                     <img src={Logo} alt="Argos Logo" className={classes.Logo}></img>
                  </Button>
                  {matches ? drawer : tabs} 
               </Toolbar>
            </AppBar>
         </ElevationScroll>
         <div className={classes.toolbarMargin} />
      </Fragment>

   );
};

export default MainNavigation;


