import React from 'react';
import { chunker } from '../../../utils/Dummydata';

const SingleAccount = ({ accounts }) => {
   return (
      <section className="module single-account">
         <h1>{accounts[i].type} account</h1>
         <ul>
            <li>Sortcode: 
                {chunker(accounts[i].sortcode, 2, '-')}
                </li>
            <li>Number: 
                {accounts[i].number}
            </li>
            <li>Currency: 
                {accounts[i].currency}
                </li>
            <li>
               Balance: 
               {accounts[i].balance} {accounts[i].currency}
            </li>
         </ul>
      </section>
   );
};

export default SingleAccount;

