import React from 'react';

import '../../Infobox/style.scss';
import './style.scss';

import PropTypes from 'prop-types';
import { myPadStart, chunker } from '../../../Utils/Dummydata';


import CardLogos from '../CardLogos';
import AccountName from '../../Infobox/AccountName';
import AccountSummary from '../../Infobox/AccountSummary';

import MultiModuleButtons from '../../../Components/UI/Buttons/MultiModuleButtons';
import SingleMultiButton from '../../../Components/UI/Buttons/SingleMultiButton';


const SingleCard = ({
   number,
   expiresMonth,
   expiresYear,
   // balance,
   // income7Days,
   // expenses7Days,
   currentUrl
}) => {
   return (

         <div className="col-6">
            <section className="module infobox card-infobox">
               <header>
                  <CardLogos type="visa" />
                  <section className="card-details">
                     <div className="card-details-row">
                        <div>
                           <span>Card number</span>
                           <p className="big">
                              {chunker(number, 4, '-')}
                              </p>
                        </div>
                     </div>
                     <div className="card-details-row card-details-row-1-2">
                        <div>
                           <span>Card holder</span>
                           <p>Tali Oppenheimer</p>
                        </div>
                        <div>
                           <span>Expiration date</span>
                           <p>
                              {myPadStart(expiresMonth, 2, 0)} / {expiresYear}
                           </p>
                        </div>
                     </div>
                  </section>
               </header>

               <AccountName />
               <AccountSummary balance={20000} income7Days={3500} expenses7Days={1500} />

               <MultiModuleButtons>
                  <SingleMultiButton text="Change PIN" href={`${currentUrl}/change-pin`} />
                  <SingleMultiButton text="Change limits" href={`${currentUrl}/change-limits`} />
               </MultiModuleButtons>
            </section>
         </div>

   );
};

SingleCard.propTypes = {
   number: PropTypes.number,
   expiresMonth: PropTypes.number,
   expiresYear: PropTypes.number,
   balance: PropTypes.number,
   income7Days: PropTypes.number,
   expenses7Days: PropTypes.number,
   currentUrl: PropTypes.string
};


export default SingleCard;
