import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { makeStyles, useTheme} from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';

import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';


const useStyles = makeStyles(theme => ({
   Logo: {
      height: "7em",
      padding: "10px",
    },
    button: {
      ...theme.typography.logIn,
      borderRadius: "40px",
      width: "15em",
      "&:hover": {
        backgroundColor: "theme.palette.primary.light",
        color: "white"
        }  
    }
}))

const RegisterForm = props => {
   const classes = useStyles()
   const theme = useTheme()

   const [email, setEmail] = useState('')
   const [password, setPassword] = useState('')

   return (
      <Grid container direction="row">
         <Grid item container direction="column" justify="center" alignItems="center">
            <Grid item>
               <Grid container direction="column">
                  <Grid item>
                     <Typography align="center" color="primary" variant="h4">Register</Typography>
                  </Grid>
                  <Grid item container style={{maxWidth: "20em"}} >
                    <FormLabel component="legend" style={{marginTop: "2em"}} >Account Type</FormLabel>
                    <RadioGroup row aria-label="position" name="position" defaultValue="top" >
                        <FormControlLabel
                        value="consumer"
                        control={<Radio color="primary" />}
                        label="Consumer"
                        labelPlacement="bottom"
                        />
                        <FormControlLabel
                        value="merchant"
                        control={<Radio color="primary" />}
                        label="Merchant"
                        labelPlacement="bottom"
                        />          
                    </RadioGroup>
                  </Grid>
                  <Grid item container direction="column" style={{maxWidth: "20em"}}>
                     <Grid item>
                        <TextField 
                           required
                           fullWidth
                           label="Email" 
                           id="email" 
                           value={email} 
                           onChange={(event) => setEmail(event.target.value) }
                           style={{marginTop: "2em"}} 
                        />
                     </Grid>
                     <Grid item>
                        <TextField 
                           required
                           fullWidth
                           label="Password" 
                           id="password"         
                           type="password"
                           autoComplete="current-password"
                           value={password}
                           onChange={(event) => setPassword(event.target.value)}
                           style={{marginTop: "2em"}}
                        />
                     </Grid>
                  </Grid>
                  <Grid item container justify="center" style={{ marginTop: "2em" }}>
                     <Button className={classes.button} variant= "contained" color="secondary">Register</Button>
                  </Grid>
               </Grid>
            </Grid>
         </Grid>
      </Grid>
   )
}

export default RegisterForm;











