import React, { Fragment, useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { useTheme} from '@material-ui/core/styles';

import HomeRoundedIcon from '@material-ui/icons/HomeRounded';
import ReceiptRoundedIcon from '@material-ui/icons/ReceiptRounded';
import CreditCardRoundedIcon from '@material-ui/icons/CreditCardRounded';
import PersonRoundedIcon from '@material-ui/icons/PersonRounded';

import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import IconButton from '@material-ui/core/IconButton';
import MenuRoundedIcon from '@material-ui/icons/MenuRounded';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import './style.scss';
import Logo from '../../../assets/Images/argoslogo.svg';

function ElevationScroll(props) {
   const { children } = props;
   const trigger = useScrollTrigger({
     disableHysteresis: true,
     threshold: 0
   });

   return React.cloneElement(children, {
     elevation: trigger ? 4 : 0,
   });
 }

 const useStyles = makeStyles(theme => ({
    toolbarMargin: {
       ...theme.mixins.toolbar,
       marginBottom: "3em"
    },
    logoContainer: {
       "&:hover": {
          backgroundColor: "transparent"
       }
    },
    Logo: {
      height: "7em",
      padding: "10px",
      [theme.breakpoints.down("md")]: {
         height: "6em"
      },
      [theme.breakpoints.down("xs")]: {
         height: "5.5em"
      }
    },
    tabContainer: {
       marginLeft: "auto"
    },
    tab: {
      ...theme.typography.tab
    },
    drawerIconContainer: {
       "&:hover": {
          backgroundColor: "transparent"
       },
       marginLeft: "auto"
    },
    drawerIcon: {
       height: "50px",
       width: "50px"
    },
    drawer: {
       backgroundColor: theme.palette.common.argosBlue
    },
    drawerItem: {
       ...theme.typography.tab,
       color: "white",
       opacity: 0.7
    },
    drawerItemSelected: {
       opacity: 1
    }
 }))


const MainNavigation = props => {
   const classes = useStyles();
   const theme = useTheme();
   const iOS = process.browser && /iPad|iPhone|iPod/.test(navigator.userAgent);
   const matches = useMediaQuery(theme.breakpoints.down("md"));

   const [openDrawer, setOpenDrawer] = useState(false);
   const [value, setValue] = useState(0);


   const handleChange = (e, newValue) => {
      setValue(newValue);
   }

   useEffect(() => {
      switch (window.location.pathname) {
         case "/panel":
            if (value !== 0){
               setValue(0);
         }
         break; 
         case "/panel/receipts":
            if (value !== 1){
               setValue(1);
         }
         break; 
         case "/panel/cards":
               if (value !== 1){
                  setValue(2);
            }
         break; 
         case "/panel/profile":
            if (value !== 1){
               setValue(3);
         }
         break;
       default:
         break;
      }
      }, [value]);

   const tabs = (
      <Fragment>
         <Tabs value={value} className={classes.tabContainer} onChange={handleChange}>
            <Tab 
               className={classes.tab} 
               component={Link} to="/panel" 
               icon={<HomeRoundedIcon />} 
               label="Home" 
            />
            <Tab 
               className={classes.tab} 
               component={Link} to="/panel/receipts" 
               icon={<ReceiptRoundedIcon />} 
               label="Receipts" 
            />
            <Tab 
               className={classes.tab} 
               component={Link} to="/panel/cards" 
               icon={<CreditCardRoundedIcon />} 
               label="Cards" 
            />
            <Tab 
               className={classes.tab} 
               component={Link} to="/panel/profile"  
               icon={<PersonRoundedIcon />}  
               label="Profile" 
            />
         </Tabs>
      </Fragment>
   );

   const drawer = (
      <Fragment>
         <SwipeableDrawer 
            disableBackdropTransition={!iOS} 
            disableDiscovery={iOS}
            open={openDrawer}
            onClose={() => setOpenDrawer(false)}
            onOpen={() => setOpenDrawer(true)}
            classes={{paper: classes.drawer}}
            >
               <List>
                  <ListItem 
                     onClick={() => {setOpenDrawer(false); setValue(0)}}  
                     divider 
                     button 
                     component={Link} 
                     to="/panel"
                     selected={value === 0}
                  >
                     <ListItemIcon>
                        <HomeRoundedIcon color="secondary" />
                     </ListItemIcon>
                     <ListItemText 
                        className={value === 0 ? [classes.drawerItem, classes.drawerItemSelected]: classes.drawerItem} 
                        disableTypography>
                           Home
                     </ListItemText>
                  </ListItem>
                  <ListItem 
                     onClick={() => {setOpenDrawer(false); setValue(1)}}    
                     divider 
                     button 
                     component={Link} 
                     to="/panel/receipts"
                     selected={value === 1}
                  >
                     <ListItemIcon>
                        <ReceiptRoundedIcon color="secondary" />
                     </ListItemIcon>
                     <ListItemText 
                        className={value === 1 ? [classes.drawerItem, classes.drawerItemSelected]: classes.drawerItem} 
                        disableTypography>
                           Receipts
                     </ListItemText>
                  </ListItem>
                  <ListItem 
                     onClick={() => {setOpenDrawer(false); setValue(2)}}    
                     divider 
                     button 
                     component={Link} 
                     to="/panel/cards"
                     selected={value === 2}
                  >
                     <ListItemIcon>
                        <CreditCardRoundedIcon color="secondary" />
                     </ListItemIcon>
                     <ListItemText 
                        className={value === 2 ? [classes.drawerItem, classes.drawerItemSelected]: classes.drawerItem}  
                        disableTypography>
                           Cards
                     </ListItemText>
                  </ListItem>
                  <ListItem 
                     onClick={() => {setOpenDrawer(false); setValue(3)}}   
                     divider 
                     button 
                     component={Link} 
                     to="/panel/profile"
                     selected={value === 3}
                  >
                     <ListItemIcon>
                        <PersonRoundedIcon color="secondary" />
                     </ListItemIcon>
                     <ListItemText 
                        className={value === 3 ? [classes.drawerItem, classes.drawerItemSelected]: classes.drawerItem}  
                        disableTypography>
                           Profile
                     </ListItemText>
                  </ListItem>
               </List>
         </SwipeableDrawer> 
         <IconButton 
            className={classes.drawerIconContainer}
            onClick={ () => setOpenDrawer(!openDrawer)} 
            disableRipple
         >
            <MenuRoundedIcon 
               color= "secondary" 
               className={classes.drawerIcon}
            />
         </IconButton>
      </Fragment>
   );

   return (
      <Fragment>
          <ElevationScroll>
            <AppBar>
               <Toolbar disableGutters>
                  <Button 
                     className={classes.logoContainer}
                     component={Link} 
                     to="/panel"
                     disableRipple 
                     onClick={() => setValue(0)}>
                     <img src={Logo} alt="Argos Logo" className={classes.Logo}></img>
                  </Button>
                  {matches ? drawer : tabs} 
               </Toolbar>
            </AppBar>
         </ElevationScroll>
         <div className={classes.toolbarMargin} />
      </Fragment>

   );
};

export default MainNavigation;


