import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';

import CardsList from '../../Components/Cards/CardsList';
import SingleCard from '../../Components/Cards/SingleCard';

const faker = require('faker');


class Cards extends Component  {
    state = {
        card : {
            isActive: faker.random.boolean(),
            number: faker.finance.mask(16, false, false),
            expiresMonth: faker.random.number({ min: 1, max: 12 }),
            expiresYear: faker.random.number({ min: 2020, max: 2030 }),
         }
    }

    render () {
        return (
            <div className="row panel-content">
                 <div className="col">
                <Switch>
                    <Route exact path={match.url} component={CardsList} />
                    <Route exact path={`${match.url}/:cardId`} component={SingleCard} />
                </Switch>
                </div>
             </div>
        );
    }
}


export default Cards;
