const InnerForm = (props) => {

   const { values, errors, touched, handleChange, handleBlur} = props;

   return (

            <Form className="register-form">
               {JSON.stringify(values)}
               <div>
              <div className="form-group">
                 <input 
                  type="text"
                  name="name"
                  placeholder="Enter your name"
                  className="form-control login-input"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.name}
                 />
                 {touched.name && errors.name && 
                     <p className="field-invalid">{errors.name}</p>
                 }
              </div>
              <div className="form-group">
                 <input 
                  type="email"
                  name="email"
                  placeholder="Please enter a valid email"
                  className="form-control login-input"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.email}
                 />
                 {touched.email && errors.email && 
                     <p className="field-invalid">{errors.email}</p>
                 }
              </div>
              <div className="form-group">
                 <input 
                  type="password"
                  name="password"
                  placeholder="Choose a password"
                  className="form-control password-input"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.password}
                 />
                 {touched.password && errors.password && (
                     <p className="field-invalid">{errors.password}</p>
                  )}
              </div>
              <div className="form-group">
                 <select
                  type="select"
                  name="user_type"
                  placeholder="Choose an account type"
                  className="form-control"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  value={values.user_type}
                 >
                    <option value="0">Consumer</option>
                    <option value="1">Merchant</option>
                 </select>
              </div>
              <p>
//                <Link to="/login">Already have an account?</Link>
//             </p>
              </div>
              <SingleModuleButton text="Create your account" type="submit" />
           </Form>

   )
}

// Wrap our form with the using withFormik HoC
const RegisterForm = withFormik({
   // Transform outer props into form values
   mapPropsToValues: props => ({ email: '', name: '', password: '', user_type: '' }),

   validationSchema: Yup.object().shape({
      name: Yup.string()
         .min(1, 'Must have a character')
         .max(255, 'Must be shorter than 255')
         .required('Must enter a name'),
      email: Yup.string()
         .required('Email is required')
         .email('This is not a valid email'),
      password: Yup.string()
         .required('Password is required')
         .min(6, 'Your password has to be at least 6 characters'),
      user_type: Yup.string()
         .required('You must select a user type')
   }),

   // Submission handler
   handleSubmit: (values, { props, setStatus }) => {
      alert(JSON.stringify(values, null, 2))
      props.onRegisterSubmit(values.email, values.name, values.password, values.user_type);
   }
})(InnerForm);

export default RegisterForm;
